import $ from"jquery";class MyPlugin{constructor(t,i){this.elem=$(t),this.options=i}init(){}}$.fn.myPlugin=t=>this.each((()=>{$.data(this,"plugin_myPlugin")||$.data(this,"plugin_myPlugin",new MyPlugin(this,t))}));class Modal{constructor(t,i){this.elem=$(t),this.options=i}init(){}}$.fn.modal=t=>this.each((()=>{$.data(this,"plugin_modal")&&$.data(this,"plugin_modal",new Modal(this,t))}));class DropDownMenu{constructor(t,i){this.elem=$(t),this.options=i}init(){this.cacheDom(),this.bindEvents()}bindEvents(){this.button.on("click",(()=>{this.rotateIcon(),this.toggle()}))}cacheDom(){this.menu=this.elem.find(".dropdown-menu"),this.button=this.elem.find(".dropdown-button"),this.icon=document.querySelector(".chevron-down")}toggle(){this.elem.hasClass("open")?this.close():this.open()}rotateIcon(){this.icon.style.transform="rotate(180deg)"}open(){}close(){this.elem.removeClass("open")}}$.fn.dropdown=t=>this.each((()=>{$.data(this,"plugin_dropdown")&&$.data(this,"plugin_dropdown",new DropDownMenu(this,t))}));